package org.greenfroyo.androidmvp_bind.app._core.delegation;import android.content.Context;import android.support.design.widget.AppBarLayout;import android.support.design.widget.CoordinatorLayout;import android.support.v7.app.AppCompatActivity;import android.support.v7.widget.Toolbar;import org.greenfroyo.androidmvp_bind.R;/** * Created by junius.ang on 8/25/2016. */public class CoreDelegateDependency {    protected Context mContext;    protected CoordinatorLayout mCoordinatorLayout;    public CoreDelegateDependency(Context mContext) {        this.mContext = mContext;        AppCompatActivity activity = (AppCompatActivity) mContext;        mCoordinatorLayout = (CoordinatorLayout) activity.findViewById(R.id.core_coordinator_layout);    }    public CoordinatorLayout getCoordinatorLayout() {        return mCoordinatorLayout;    }    public void setCoordinatorLayout(CoordinatorLayout mCoordinatorLayout) {        this.mCoordinatorLayout = mCoordinatorLayout;    }    public Context getContext() {        return mContext;    }}